import { isValidElement } from 'react';
import useToken from './useToken';

const Auth = {
    //const { token, setToken } = useToken();

    isAuthenticated: false,
    authenticate() {
        this.isAuthenticated = true;
    },
    signout() {
        this.isAuthenticated = false;
    },
    getAuth() {
        return this.authenticate;
    },

    Authenticate(){
        const userToken = sessionStorage.getItem('userToken');
        console.log("the usertoke tyoe is: ", typeof userToken);
        console.log("the userToken is: ", userToken);
        if(useToken === null) {
            console.log("returning true: NOT authenticated!!");
            this.isAuthenticated = false;
        }
        else{
            console.log("returning false: authenticated!!");
            this.isAuthenticated =  true; 
        }
    },

    IsValid(){
        const userToken = sessionStorage.getItem('userToken');
        console.log("useToken === null ", useToken === null);
        if(useToken === null) return false
        else return true;
    }
};
export default Auth;
