import React from "react";
import { useState } from 'react';
import axios from "axios";
import {
    Grid,
    Typography,
    InputAdornment,
    Container,
    CssBaseline,
    Card,
    CardContent,
    CardHeader,
    CardActions,
    Button,
    TextField,
    Link,
} from "@material-ui/core";
import LockOpenIcon from '@material-ui/icons/LockOpen';

import { makeStyles } from "@material-ui/core/styles";

import { green } from "@material-ui/core/colors";

import AlternateEmailIcon from "@material-ui/icons/AlternateEmail";

const useStyle = makeStyles((them) => ({
    root: {
        height: "100vh",
        width: "100%",
    },
    resetButton: {
        backgroundColor: green[700],
        color: them.palette.primary.contrastText,
        marginTop: them.spacing(4),
        marginBottom: them.spacing(4),
        "&:hover": {
            backgroundColor: green[600],
            color: them.palette.primary.contrastText,
        },
        "&:active": {
            backgroundColor: green[700],
            color: them.palette.primary.contrastText,
        },
    },
    MainTitle: {
        color: green[700],
        [them.breakpoints.down("sm")]: {
            fontSize: 24,
        },
    },
}));

export default function PasswordResetPage(props) {
    const classes = useStyle();

    const [email, setEmailInput] = useState(''); // '' is the initial state value
    const [password, setPasswordInput] = useState(''); // '' is the initial state value


    const submitOnClick = (event) => {
        console.log("the value of email input is: ", email);

        axios.defaults.baseURL = 'http://localhost:9191';

        axios.post('/users/sendMail', {
            Email: email
        })
            .then(function (response) {
                console.log(response);
            })
            .catch((error) => {
                console.log(error);
            })
    }


    return (
        <>
            <CssBaseline />
            <Container>
                <Grid
                    container
                    justifyContent="center"
                    alignContent="center"
                    className={classes.root}
                >
                    <Grid item xs={12} md={8}>
                        <Card style={{ backgroundColor: 'transparent', boxShadow: 'none' }}>
                            <CardHeader
                                title={
                                    <Typography variant="h4" className={classes.MainTitle}>
                                        Password Recovery
                  </Typography>
                                }
                                subheader="Enter your email and instructions will be sent to you!"
                            />
                            <CardContent>
                                <TextField
                                    value={password}
                                    onInput={e => setPasswordInput(e.target.value)}
                                    label="Password"
                                    variant="outlined"
                                    type='password'
                                    className={classes.InputFieldsText}
                                    InputProps={{
                                        startAdornment: (
                                            <InputAdornment position="start">
                                                <LockOpenIcon />
                                            </InputAdornment>
                                        ),
                                    }}
                                    fullWidth={true}
                                />
                            </CardContent>
                            <CardActions>
                                <Button
                                    onClick={() => submitOnClick()}
                                    variant="contained"
                                    className={classes.resetButton}
                                    fullWidth
                                >
                                    Reset
                </Button>
                            </CardActions>
                        </Card>
                        <Typography>
                            Â© 2021 All Rights Reserved. CORK is a product of Designreset.
              <Link style={{ color: green[700] }}>Cookie Preferences, Privacy</Link>, and
              <Link style={{ color: green[700] }}> Terms</Link>.
            </Typography>
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}
