import React from 'react';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

import {
    IconButton,
    Box
} from "@material-ui/core";
import { green } from "@material-ui/core/colors";
import { TextField } from "@material-ui/core";
import { withStyles } from "@material-ui/core/styles";



import EditIcon from "@material-ui/icons/Edit";

const useStyle = theme => ({
    root: {
        height: "100vh",
        width: "100%",
    },
    loginButton: {
        backgroundColor: green[700],
        color: theme.palette.primary.contrastText,
        marginTop: theme.spacing(4),
        marginBottom: theme.spacing(4),
        "&:hover": {
            backgroundColor: green[600],
            color: theme.palette.primary.contrastText,
        },
        "&:active": {
            backgroundColor: green[700],
            color: theme.palette.primary.contrastText,
        },
    },
    MainTitle: {
        color: green[700],
        [theme.breakpoints.down('sm')]: {
            fontSize: 24
        }
    },
});

class ReactPopup extends React.Component {
    constructor(props) {
        super(props)
    this.state = {
        email: '',
        password: ''
    }
}

    render() {
        const { classes } = this.props;

        return (<>

            <Popup trigger={<IconButton style={{ color: green[700] }}>
                                <EditIcon />
                            </IconButton>}
                position="center">
                <div>Popup content here !!</div>
                <Box className={classes.InputFields}>
                    <TextField
                        value={"firstname"}
                        onInput={e => setFirstNameInput(e.target.value)}
                        label="First Name"
                        variant="outlined"
                        className={classes.InputFieldsText}
                        InputProps={{
                            startAdornment: (
                                <InputAdornment position="start">
                                    <AccountCircleIcon />
                                </InputAdornment>
                            ),
                        }}
                        fullWidth={true}
                    />
                    <TextField
                        value={"lastname"}
                        onInput={e => setSecondNameInput(e.target.value)}
                        label="Last Name"
                        variant="outlined"
                        className={classes.InputFieldsText}
                        InputProps={{
                            startAdornment: (
                                <InputAdornment position="start">
                                    <AccountCircleIcon />
                                </InputAdornment>
                            ),
                        }}
                        fullWidth={true}
                    />

                    <TextField
                        value={"email"}
                        onInput={e => setEmailInput(e.target.value)}
                        label="Email"
                        variant="outlined"
                        className={classes.InputFieldsText}
                        InputProps={{
                            startAdornment: (
                                <InputAdornment position="start">
                                    <AlternateEmailIcon />
                                </InputAdornment>
                            ),
                        }}
                        fullWidth={true}
                    />
                </Box>
            </Popup>
        </>)
    };
};
export default withStyles(useStyle, { withTheme: true })(ReactPopup);
