import React from "react";
import ReactDOM from 'react-dom'; 
import  { useState } from "react";
import axios from "axios";
import {
    Container,
    Box,
    Link,
    TextField,
    InputAdornment,
    FormControlLabel,
    Checkbox,
    Button
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Typography } from "@material-ui/core";
import { green } from '@material-ui/core/colors';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import AlternateEmailIcon from '@material-ui/icons/AlternateEmail';
import LockOpenIcon from '@material-ui/icons/LockOpen';

const useStyle = makeStyles((them) => ({
    Container: {
        padding: `${them.spacing(4)}px 0px`,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        maxHeight: 600
    },
    InputFields: {
        marginTop: them.spacing(8),
        marginBottom: them.spacing(8),
        width: "100%",
    },
    InputFieldsText: {
        marginBottom: them.spacing(4),
        maxWidth: 600,
        display: 'block',
        color: `${green[700]} !important`,
        '&:hover': {
            color: green[700],
        },
        '&:active': {
            color: green[700],
            borderColor: green[700],
        }
    },
    CheckboxControler: {
        color: green[700],
    },
    Button: {
        display: 'block',
        color: 'white',
        backgroundColor: green[700],
        '&:hover': {
            backgroundColor: green[900],
        }
    }
}));

class NameForm extends React.Component {

    // classes = useStyle();

    constructor(props) {
      super(props);
      //const classes = useStyle();


      this.state = {value: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      alert('A name was submitted: ' + this.state.value);
      event.preventDefault();
    }

    render() {

         const { classes } = this.props;

      return (
        <TextField
        label="Email"
        variant="outlined"
        className={classes.InputFieldsText}
        InputProps={{
            startAdornment: (
                <InputAdornment position="start">
                    <AlternateEmailIcon />
                </InputAdornment>
            ),
        }}
        fullWidth={true}
    />
      );
    }
  }

  export default function DisplaySignUp(){

    NameForm.render();
    // render() {
    //     return (
    // ReactDOM.render(
    //     <NameForm />,
    //     document.getElementById('root')
    //   );
    //     );
  }
