import useToken from './useToken';

const Auth = {
    isAuthenticated: false,
    authenticate() {
        this.isAuthenticated = true;
    },
    signout() {
        this.isAuthenticated = false;
    },
    GetAuth() {
        const { token, setToken } = useToken();
        console.log("the token right now is: ", token);
        return this.isAuthenticated;
    }
};
export default Auth;
