import React from "react";
import {
    Container,
    Box,
    Link,
    TextField,
    InputAdornment,
    FormControlLabel,
    Checkbox,
    Button
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Typography } from "@material-ui/core";
import { green } from '@material-ui/core/colors';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import AlternateEmailIcon from '@material-ui/icons/AlternateEmail';
import LockOpenIcon from '@material-ui/icons/LockOpen';

const useStyle = makeStyles((them) => ({
    Container: {
        padding: `${them.spacing(4)}px 0px`,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        maxHeight: 600
    },
    InputFields: {
        marginTop: them.spacing(8),
        marginBottom: them.spacing(8),
        width: "100%",
    },
    InputFieldsText: {
        marginBottom: them.spacing(4),
        maxWidth: 600,
        display: 'block',
        color: `${green[700]} !important`,
        '&:hover': {
            color: green[700],
        },
        '&:active': {
            color: green[700],
            borderColor: green[700],
        }
    },
    CheckboxControler: {
        color: green[700],
    },
    Button: {
        display: 'block',
        color: 'white',
        backgroundColor: green[700],
        '&:hover': {
            backgroundColor: green[900],
        }
    }
}));

class NameForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: '' };
        this.password = { value: 'Password' }

        this.handleNameChange = this.handleNameChange.bind(this);
        //this.handlePasswordChange = this.handlePasswordChange.bind(this);
        //this.handlePasswordChange = this.handlePasswordChange(this);

        this.handleSubmit = this.handleSubmit.bind(this);

        const [pass, setPass] =
            useState(function generateRandomInteger() {
                return "hello";

            });
    }



    handleNameChange(event) {
        this.setState({ value: event.target.value });
    }

    handlePasswordChange(event) {
        console.log("the func is handlePasswordChange()")
        this.password = event.target.value;
        //this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
    }


    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Name:
            <input type="text" value={this.state.value} onChange={this.handleNameChange} />
                </label>
                <label>
                    Password:
            <input type="text" value={this.pass} onChange={(event) => this.handlePasswordChange(event)} />
                </label>
                <input type="submit" value="Submit" />
            </form>
        );
    }
}
export default function SingUp() {

    return (<>
        <NameForm></NameForm>
    </>
    );
}

