import React from 'react'
import PropTypes from 'prop-types'
import {
  BrowserRouter as Router,
  Route,
  Switch
} from 'react-router-dom'

import LoginPage from "./pages/LoginPage";
import SignUpPage from "./pages/singUpPage";
import PasswordRecoverPage from "./pages/PasswordRecoveryPage";
import PasswordResetPage from "./pages/PasswordReset";
import MapUser from "./pages/MapUser";
import Auth from './auth/Auth';
import * as authentication from "./auth/authentication";


class App extends React.Component {
  constructor () {
    super()

    let authState = authentication.getAuthState()
    this.state = {
      auth: {
        loggedIn: authState.userToken !== null,
        username: authState.username,
        userToken: authState.userToken,
        role: authState.role
      }
    }

    this.login = this.login.bind(this)
    this.logout = this.logout.bind(this)
  }

  getChildContext () {
    return {
      auth: {
        loggedIn: this.state.auth.loggedIn,
        username: this.state.auth.username,
        userToken: this.state.auth.userToken,
        role: this.state.auth.role
      }
    }
  }

  login (username, password) {
    return authentication.login(username, password)
        .then(result => {
          this.setState({
            auth: {
              ...this.state.auth,
              loggedIn: true,
              username: result.username,
              userToken: result.userToken,
              role: result.role
            }
          })
        })
        .catch(err => {
          this._logout()
          return Promise.reject(err)
        })
  }

  logout () {
    return authentication.logout().then(result => {
      this._logout()
    })
  }

  render () {
    return (
        <Router>
        <div>

          <Switch>
            <Route exact path="/login" component={LoginPage}  >
            </Route>
            <Route path="/sign-up" component={SignUpPage}>
            </Route>
            <Route path="/forgot-password" component={PasswordRecoverPage}>
            </Route>
            <Route path="/password-reset" component={PasswordResetPage}>
            </Route>
            <PrivateRoute path="/home-page" > <MapUser/></PrivateRoute>

          </Switch>
        </div>
      </Router>
    )
  }

  _logout () {
    this.setState({
      auth: {
        ...this.state.auth,
        loggedIn: false,
        username: null,
        userToken: null,
        role: null
      }
    })
  }

}

App.childContextTypes = {
  auth: PropTypes.shape({
    loggedIn: PropTypes.bool,
    username: PropTypes.string,
    userToken: PropTypes.string,
    role: PropTypes.string
  })
}

export default App
